mapper.select:
  	select 

	from t_goods_category where  parent_id=#{categoryId}
	and state <> #{notState}
	order by update_time desc

  	select 

	from t_goods_category where  parent_id=#{categoryId}
	and state = #{state}
	order by update_time desc

  	select 

	from t_goods_category where  parent_id = 0 and state <> #{notState,jdbcType=INTEGER}
	order by update_time desc

  	select 

	from t_goods_category where  parent_id = 0 and state = #{notState,jdbcType=INTEGER}
	order by update_time desc
